import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { User } from '../models/User';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http:HttpClient) { }

  uri = 'http://localhost:8080/users';

  login(username: string, password: string) {
    const data = {
      username : username,
      password : password
    }
    return this.http.post<User>(`${this.uri}/login`, data);
  }

  logout() {
    localStorage.clear();
  }

  getUser(username: string) {
    return this.http.post<User>(`${this.uri}/getUser`, {username: username});
  }

  uploadFile(file: File) {
    const formData: FormData = new FormData();
    formData.append('file', file);
    return this.http.post<string>(`${this.uri}/uploadFile`, formData, {responseType: 'text'} );
  }

  register(username: string, password: string, type: string, firstname: string, lastname: string, gender: string, address: string,
    email: string, image: string, typeOfSchool: string, currentGrade: number) {
      /*
      formData.append('username', username);
      formData.append('password', password);
      formData.append('type', type);
      formData.append('firstname', firstname);
      formData.append('lastname', lastname);
      formData.append('gender', gender);
      formData.append('email', email);
      formData.append('address', address);
      formData.append('typeOfSchool', typeOfSchool);
      formData.append('currentGrade', String(currentGrade));

      u.username = username;
      u.password = password;
      u.type = type;
      u.firstname = firstname;
      u.lastname = lastname;
      u.gender = gender;
      u.address = address;
      u.email = email;
      u.typeOfSchool = typeOfSchool;
      u.currentGrade = currentGrade;
      formData.append("user", JSON.stringify(u));
*/
      const data = {
        username: username,
        password: password,
        type: type,
        firstname: firstname,
        lastname: lastname,
        gender: gender,
        address: address,
        email: email,
        image: image,
        typeOfSchool: typeOfSchool,
        currentGrade: currentGrade,
      }

      return this.http.post<number>(`${this.uri}/addUser`, data);
  }

  changePassword(username: string, password: string) {
    const data = {
      username : username,
      password : password
    }
    return this.http.post<number>(`${this.uri}/changePassword`, data);
  }

}
