import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { AppComponent } from '../app.component';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  constructor(private userService:UserService, private router:Router, private app:AppComponent) { }

  ngOnInit(): void {
    let user = localStorage.getItem('loggedIn');
    if(user) this.loggedIn = true;
    console.log(window.location.pathname);console.log(window.location.pathname);

  }

  username: string = "";
  password: string = "";
  type: string = "";
  firstname: string = "";
  lastname: string = "";
  gender: string = "";
  address : string = "";
  email : string = "";
  typeOfSchool: string = "";
  currentGrade: number = 0;
  imagePath: string = "";
  image: File | undefined;
  message: string = "";
  loggedIn: boolean= false;
  isImageValid: boolean = false;

  register() {
    /*if(this.username == "" || this.password == "" || this.type == "" || this.firstname == "" || this.lastname == "" || this.address == ""
     || this.gender == "" || this.email == "" || this.typeOfSchool == "" ||  this.currentGrade == 0) {
      this.message = "Please fill out all fields before continuing."; 
      return;
    }*/
    this.userService.getUser(this.username).subscribe((resp) => {
      if(resp) this.message = "Username is already taken."
      else {
        console.log(window.location.pathname);
        return;
        /*if(!this.image) this.image = new File() 
        this.userService.uploadFile(this.image).subscribe(resp=>{
          if(!resp) alert("Error while uploading profile image");
          else {
            this.imagePath = resp;
            this.userService.register(this.username, this.password, this.type, this.firstname, this.lastname, this.gender, this.address,
              this.email, this.imagePath, this.typeOfSchool, this.currentGrade).subscribe((resp)=>{
                if(resp == 0) {
                  this.message = "Invalid data";
                }
                else {
                  this.message = "";
                  if(this.type == "student") {
                    alert("You have successfully signed up!");
                    this.router.navigate(['/login']);
                  } else {
                    localStorage.setItem("username", this.username);
                    this.router.navigate(['/registerTutor']);
                  }
                }
              })
          }
        })*/
      }
    });
  }

  onChange(event: any) {
    this.isImageValid = true;
    (event.target as HTMLInputElement).files;
    const filelist = (<HTMLInputElement>event.target).files;
    if (filelist != null) this.image = filelist[0];
    else this.image = undefined;
    if(this.image) {
      if(this.image.type != 'image/jpg' && this.image.type != 'image/png') {
        this.isImageValid = false;
        alert('The type of the image is not valid. The image must be either .png or .jpg.');
        return;
      }
      const reader = new FileReader();
      reader.onload = (e: any) => {
        const img = new Image();
        img.src = e.target.result;
        img.onload = () => {
          const width = img.width;
          const height = img.height;
          if (width < 100 || height < 100 || width > 300 || height > 300) {
            this.isImageValid = false;
            alert('The dimensions of the image is not valid. The image must be between 100x100 and 300x300.');
            return;
          }
        };
      };
      reader.readAsDataURL(this.image);
    } else {
      this.isImageValid = false;
    }
  }

  logout() {
    this.app.logout();
  }

  profile() {
    this.app.profile();
  }
}
