import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { AppComponent } from '../app.component';
import { User } from '../models/User';

@Component({
  selector: 'app-security-question',
  templateUrl: './security-question.component.html',
  styleUrls: ['./security-question.component.css']
})
export class SecurityQuestionComponent {
  constructor(private service: UserService, public app: AppComponent) { }

  ngOnInit() {
    let item = localStorage.getItem('loggedIn')
    if(item) this.user = JSON.parse(item);
    if(this.user.username == "") this.loggedIn = false;
    else this.loggedIn = true;
    item = localStorage.getItem("question");
    if(item) this.question = JSON.parse(item);
    localStorage.removeItem("question");
  }

  loggedIn: boolean = false;
  user: User = new User();
  question: string = "";
  answer: string = "";
  new: string = "";
  confirm: string = "";
  message: string = "";

  save() {
    if(this.answer == "" || this.new == "" || this.confirm == "") {
      this.message = "Please fill out all fields before continuing."; 
      return;
    }
    this.service.login(this.user.username, this.answer).subscribe((user: User)=>{
      if(!user){
        this.message = 'The old password is not correct!';
      } else {
        if(this.new !== this.confirm) {
          this.message = "The new password and the cofirmed password do not match!"
        }
        else {
          this.service.changePassword(this.user.username, this.new).subscribe(resp=>{
            if (resp>0) {
              this.message = '';
              alert("You have successfully changed the password.")
              this.app.profile();
            }
            else 
              this.message = "Error";
          })
        }
      }
    })
  }
}
<h2 class="text-center">Change password</h2>
<div class="text-center err" *ngIf="loggedIn==false">
    <br>You are not logged in!<br><br>
    <input type='button' value='Log in' [routerLink]="['/login']" class="button btn" style="margin-right: 10px;">
    <input type='button' value='Sign up' [routerLink]="['/signup']" class="button btn">
</div>
<form *ngIf="loggedIn==true">
    <table>
        <tr>
            <td>{{question}}</td>
            <td>
                <input type='text' name='answer' [(ngModel)]='answer' class="form-control">
            </td>
        </tr>
        <tr>
            <td>New password:</td>
            <td>
                <input type='password' name='new' [(ngModel)]='new' class="form-control">
            </td>
        </tr>
        <tr>
            <td>Confirm new password:</td>
            <td>
                <input type='password' name='confirm' [(ngModel)]='confirm' class="form-control">
            </td>
        </tr>
        <tr>
            <td colspan="2" class="err text-center">
                {{message}}
            </td>
        </tr>
        <tr>
            <td colspan='2' class="text-center">
                <input type='button' value='Save' (click)='save()' class="button btn">
            </td>
        </tr>
    </table>
</form>
