import { Component, OnInit } from '@angular/core';
import { Chart } from 'chart.js/auto';
import { UserService } from '../services/user.service';
import { RegRequestService } from '../services/reg_request.service';

@Component({
  selector: 'app-charts',
  templateUrl: './charts.component.html',
  styleUrls: ['./charts.component.css']
})
export class ChartsComponent implements OnInit{

  constructor(private userService:UserService, private regRequestService:RegRequestService) {}

  ngOnInit(): void {
    this.userService.getGenderCount().subscribe(resp=>{
      let genderStudents = document.getElementById('genderStudents') as HTMLCanvasElement;
      let data = {
        labels: ['M','F'],
        datasets: [{data: [resp[0], resp[1]], backgroundColor: ['rgb(54, 162, 235)','rgb(255, 99, 132)'], hoverOffset: 4}]
      };
      if(genderStudents != null) {
        new Chart(genderStudents, {type: 'pie',data: data});
      }
      let genderTutors = document.getElementById('genderTutors') as HTMLCanvasElement;
      data.datasets = [{data: [resp[2], resp[3]], backgroundColor: ['rgb(54, 162, 235)', 'rgb(255, 99, 132)'], hoverOffset: 4}];
      if(genderTutors != null) {
        new Chart(genderTutors, {type: 'pie',data: data});
      }
    })
    this.regRequestService.getAgeCount().subscribe(resp=>{
      let age = document.getElementById('age') as HTMLCanvasElement;
      let data = {
        labels: ['Primary school - 1-4 grade','Primary school - 5-8 grade','High school'],
        datasets: [{lable: "", data: [resp[0], resp[1], resp[2]], backgroundColor: ['rgb(54, 162, 235)','rgb(255, 99, 132)','rgb(255, 205, 86)'], hoverOffset: 4}]
      };
      if(age != null) {
        new Chart(age, {type: 'bar',data: data,
        options: {
          scales: {
            y: {
              beginAtZero: true
            }
          },
          plugins: {
            legend: {
              display: false
            }
          }
        }
        });
      }
    })
  }


}
