import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { AppComponent } from '../app.component';

@Component({
  selector: 'app-login-admin',
  templateUrl: './login-admin.component.html',
  styleUrls: ['./login-admin.component.css']
})
export class LoginAdminComponent {

  constructor(private service: UserService, public app: AppComponent) { }

  ngOnInit(): void {
    let user = localStorage.getItem('loggedIn');
    if(!user) this.loggedIn = false;
    else this.loggedIn = true;
  }

  username: string = '';
  password: string = '';
  message: string = '';
  loggedIn: boolean = false;

  login(){
    if(!this.username || !this.password) {
      this.message = "Please fill out all fields before continuing."; 
      return;
    }
    this.service.login(this.username, this.password).subscribe(user=>{
      if(!user){
        this.message = 'Invalid data';
      } else {
        this.message = '';
        localStorage.setItem('loggedIn', JSON.stringify(user));
        this.app.ngOnInit();
        this.app.profile();
      }
    })
  }

  logout() {
    this.app.logout();
  }

  profile() {
    this.app.profile();
  }
}
<h1 class="text-center">Login</h1>
<div class="text-center err" *ngIf="loggedIn==true">
    <br>You are already logged in!<br><br>
    <input type='button' value='Log out' (click)="logout()" class="button btn" style="margin-right: 10px;">
    <input type='button' value='See profile' (click)="profile()" class="button btn">
</div>
<form *ngIf="loggedIn==false">
    <table>
        <tr>
            <td>Username:</td>
            <td>
                <input type='text' name='username' [(ngModel)]='username' class="form-control">
            </td>
        </tr>
        <tr>
            <td>Password:</td>
            <td>
                <input type='password' name='password' [(ngModel)]='password' class="form-control">
            </td>
        </tr>
        <tr>
            <td colspan="2" class="err text-center">
                {{message}}
            </td>
        </tr>
        <tr>
            <td colspan='2' class="text-center">
                <input type='button' value='Login' (click)='login()' class="button btn">
            </td>
        </tr>
    </table>
</form>
