import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  constructor(private router:Router, private service:UsersService) {}

  loggedIn: boolean;
  type: number;

  ngOnInit(): void {
    let user = localStorage.getItem('loggedIn');
    if(!user) this.loggedIn = false;
    else  {
      this.loggedIn = true;
      this.type = JSON.parse(user).type;
    }
  }

  logout() {
    this.service.logout();
    this.loggedIn = false;
    alert("You have succesfully logged out.");
    this.router.navigate(['']);
  }

  profile() {
    let user:User = JSON.parse(localStorage.getItem('loggedIn'));
    if(user.type == 1) this.router.navigate(['/doctorProfile']);
    if(user.type == 2) this.router.navigate(['/patientProfile']);
    if(user.type == 3) this.router.navigate(['/managerProfile']);
  }
}
