import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { AppComponent } from '../app.component';
import { User } from '../models/User';
import { RegRequest } from '../models/RegRequest';
import { RegRequestService } from '../services/reg_request.service';

@Component({
  selector: 'app-update-profile',
  templateUrl: './update-profile.component.html',
  styleUrls: ['./update-profile.component.css']
})
export class UpdateProfileComponent {

  constructor(private userService:UserService, private regRequestService: RegRequestService, private router:Router, private app:AppComponent) { }

  firstname: string = "";
  lastname: string = "";
  address: string = "";
  contact : string = "";
  email : string = "";
  typeOfSchool: string = "";
  currentGrade: number = 0;
  subjects: string[] = [];
  subjectsStr: string = "";
  age: string = "";
  imagePath: string = "";
  image: File | undefined;
  other: string = "";
  message: string = "";
  loggedIn: boolean = false;
  isImageValid: boolean = false;
  user: User = new User();
  request: RegRequest = new RegRequest();

  ngOnInit(): void {
    let item = localStorage.getItem('loggedIn');
    if(item) this.user = JSON.parse(item);
    if(this.user.username == "") this.loggedIn = false;
    else {
      this.loggedIn = true;
      if(this.user.type==="tutor") {
        item = localStorage.getItem('tutorRequest');
        if(item) this.request = JSON.parse(item);
      }
      this.firstname = this.user.firstname;
      this.lastname = this.user.lastname;
      this.address = this.user.address;
      this.contact = this.user.contact;
      this.email = this.user.email;
      if(this.user.type === "student") {
        this.typeOfSchool = this.user.typeOfSchool;
        this.currentGrade = this.user.currentGrade;
      } else if(this.user.type === "tutor") {
        this.age = this.request.age;
      }
    }
  }

  update() {
    this.subjects.forEach(s=>this.subjectsStr+=s+', ');
    if(this.other != "") this.subjectsStr += this.other + ', ';
    this.subjectsStr = this.subjectsStr.slice(0, this.subjectsStr.length-2);
    this.userService.update(this.user.username, this.firstname, this.lastname, this.address, this.contact, this.email, this.typeOfSchool,
     this.currentGrade).subscribe((resp)=>{
      if (!resp && resp!=1) alert("Error!");
      else {
        this.message = '';
        this.user.firstname = this.firstname;
        this.user.lastname = this.lastname;
        this.user.address = this.address;
        this.user.contact = this.contact;
        this.user.email = this.email;
        if(this.user.type === "student") {
          this.user.typeOfSchool = this.typeOfSchool;
          this.user.currentGrade = this.currentGrade;
          localStorage.setItem('loggedIn', JSON.stringify(this.user));
          alert("You have successfully updated your profile!");
          this.router.navigate(['/profile']);
        } else if(this.user.type === "tutor") {
          this.regRequestService.update(this.subjectsStr, this.age).subscribe((resp)=>{
            if (!resp && resp!=1) alert("Error!");
            else {
              this.request.subjects = this.subjectsStr;
              this.request.age = this.age;
              localStorage.setItem('tutorRequest', JSON.stringify(this.request));
              alert("You have successfully updated your profile!");
              this.router.navigate(['/profile']);
            }
          })
        }
      }
    })
  }

  onChange(event: any) {
    this.isImageValid = true;
    (event.target as HTMLInputElement).files;
    const filelist = (<HTMLInputElement>event.target).files;
    if (filelist != null) this.image = filelist[0];
    else this.image = undefined;
    if(this.image) {
      if(this.image.type != 'image/jpg' && this.image.type != 'image/png') {
        this.isImageValid = false;
        alert('The type of the image is not valid. The image must be either .png or .jpg.');
        return;
      }
      const reader = new FileReader();
      reader.onload = (e: any) => {
        const img = new Image();
        img.src = e.target.result;
        img.onload = () => {
          const width = img.width;
          const height = img.height;
          if (width < 100 || height < 100 || width > 300 || height > 300) {
            this.isImageValid = false;
            alert('The dimensions of the image is not valid. The image must be between 100x100 and 300x300.');
            return;
          }
        };
      };
      reader.readAsDataURL(this.image);
    } else {
      this.isImageValid = false;
    }
  }

}
