import { Component } from '@angular/core';
import { User } from '../models/User';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent {

  ngOnInit(): void {
    let item = localStorage.getItem('loggedIn');
    if(item) this.user = JSON.parse(item);
    if(this.user.username == "") this.loggedIn = false;
    else this.loggedIn = true;
    this.apiUrl = `http://localhost:8080/api/images/${this.user.image}`;
    this.imageUrl = this.apiUrl;
    console.log(this.apiUrl, this.imageUrl);
  }

  user: User = new User();
  message: string = "";
  loggedIn: boolean = false;
  apiUrl: string = "";
  imageUrl: string = "";

}
