import { Component } from '@angular/core';
import { TutorService } from '../services/tutor.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register-tutor',
  templateUrl: './register-tutor.component.html',
  styleUrls: ['./register-tutor.component.css']
})
export class RegisterTutorComponent {

  constructor(private tutorService:TutorService, private router:Router) { }

  ngOnInit(): void {
    let user = localStorage.getItem('loggedIn');
    if(user) this.loggedIn = true;
  }

  username: string = "";
  cv: string = "";
  subjects: string[] = [];
  age: string = "";
  where: string = "";
  message: string = "";
  loggedIn: boolean= false;

  register() {
    if(this.username == "" || this.cv == "" || this.subjects.length==0 || this.age == "" || this.where == "") {
      this.message = "Please fill out all fields before continuing."; 
      return;
    }
    this.userService.getUser(this.username).subscribe((resp) => {
      if(resp) this.message = "Username is already taken."
      else {
        this.userService.register(this.username, this.password, this.type, this.firstname, this.lastname, this.gender, this.address,
        this.email, this.image, this.typeOfSchool, this.currentGrade).subscribe((resp)=>{
          if(resp == 0) {
            this.message = "Invalid data";
          }
          else {
            this.message = "";
            if(this.type == "student") {
              alert("You have successfully signed up!");
              this.router.navigate(['/login']);
            } else {
              localStorage.setItem("username", this.username);
              this.router.navigate(['/registerTutor']);
            }
          }
        })
      }
    });
  }

}
