import { Component } from '@angular/core';
import { TutorService } from '../services/tutor.service';
import { Router } from '@angular/router';
import { AppComponent } from '../app.component';

@Component({
  selector: 'app-register-tutor',
  templateUrl: './register-tutor.component.html',
  styleUrls: ['./register-tutor.component.css']
})
export class RegisterTutorComponent {

  constructor(private tutorService:TutorService, private router:Router, private app:AppComponent) { }

  ngOnInit(): void {
    let user = localStorage.getItem('loggedIn');
    if(user) this.loggedIn = true;
  }

  cv: string = "";
  subjects: string[] = [];
  age: string = "";
  where: string = "";
  other: string = "";
  message: string = "";
  loggedIn: boolean= false;

  register() {
    let username: string;
    let item = localStorage.getItem("username");
    if(item) username = item;
    if(this.cv == "" || this.subjects.length==0 || this.age == "" || this.where == "") {
      this.message = "Please fill out all fields before continuing."; 
      return;
    }
    let subjectsStr = "";
    this.subjects.forEach(s=>subjectsStr+=s+' ');
    if(this.other != "") subjectsStr += this.other + ' ';
    this.tutorService.register(username, this.cv, subjectsStr, this.age, this.where).subscribe((resp) => {
      if(resp == 0) {
        this.message = "Invalid data";
      }
      else {
          alert("You have successfully signed up!");
          this.router.navigate(['/login']);
      }
    });
  }

  logout() {
    this.app.logout();
  }

  profile() {
    this.app.profile();
  }

}
