import { Component } from '@angular/core';
import { UserService } from '../services/user.service';
import { Router } from '@angular/router';
import { AppComponent } from '../app.component';
import { TeacherService } from '../services/teacher.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  constructor(private userService:UserService, private teacherService:TeacherService, private router:Router, private app:AppComponent) { }

  ngOnInit(): void {
    let user = localStorage.getItem('loggedIn');
    if(user) this.loggedIn = true;
  }

  username: string = "";
  password: string = "";
  type: string = "";
  firstname: string = "";
  lastname: string = "";
  gender: string = "";
  address : string = "";
  email : string = "";
  typeOfSchool: string = "";
  currentGrade: string = "";
  image: string = "";
  message: string = "";
  loggedIn: boolean= false;

  signup() {
    if(this.username == "" || this.password == "" || this.type == "" || this.firstname == "" || this.lastname == "" || this.address == ""
     || this.gender == "" || this.email == "" /* || this.image == ""*/ || this.typeOfSchool == "" ||  this.currentGrade == "") {
      this.message = "Please fill out all fields before continuing."; 
      return;
    }
    this.userService.getUser(this.username).subscribe((resp) => {
      if(!resp) this.message = "Username is already taken."
      else {
        this.userService.register(this.username, this.password, this.type, this.firstname, this.lastname, this.gender, this.address,
        this.email, this.image, this.typeOfSchool, this.currentGrade).subscribe((resp)=>{
          if(resp == 0) {
            this.message = "Invalid data";
          }
          else {
            this.message = "";
            if(this.type == "student") {
              alert("You have successfully signed up!");
              this.router.navigate(['/login']);
            } else {
              localStorage.setItem("username", this.username);
              this.router.navigate(['/registerTeacher']);
            }
          }
        })
      }
    });
  }

  logout() {
    this.app.logout();
  }

  profile() {
    this.app.profile();
  }
}
