import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { User } from '../models/User';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http:HttpClient) { }

  uri = 'http://localhost:8080/users';

  login(username: string, password: string) {
    const data = {
      username : username,
      password : password
    }
    return this.http.post<User>(`${this.uri}/login`, data);
  }

  getUser(username: string) {
    return this.http.post<User>(`${this.uri}/getUser`, username);
  }

  register(username: string, password: string, type: string, firstname: string, lastname: string, gender: string, address: string,
    contact: string, email: string, image: string, typeOfSchool: string, currentGrade: number) {
      const data = {
        username: username,
        password: password,
        type: type,
        firstname: firstname,
        lastname: lastname,
        gender: gender,
        address: address,
        contact: contact,
        email: email,
        image: image,
        typeOfSchool: typeOfSchool,
        currentGrade: currentGrade,
      }
      return this.http.post<number>(`${this.uri}/addUser`, data);
  }

  changePassword(username: string, password: string) {
    const data = {
      username : username,
      password : password
    }
    return this.http.post<number>(`${this.uri}/changePassword`, data);
  }

  update(username: string, type: string, firstname: string, lastname: string, address: string, contact: string, email: string, typeOfSchool: string, currentGrade: number) {
      const data = {
        username: username,
        type: type,
        firstname: firstname,
        lastname: lastname,
        address: address,
        contact: contact,
        email: email,
        typeOfSchool: typeOfSchool,
        currentGrade: currentGrade,
      }
      return this.http.post<number>(`${this.uri}/updateUser`, data);
  }

  updateImage(username: string, image: string) {
    const data = {
      username : username,
      image : image
    }
    return this.http.post<number>(`${this.uri}/updateImage`, data);
  }

  getStudents() {
    return this.http.get<User[]>(`${this.uri}/getStudents`);
  }

  getApprovedTutors() {
    return this.http.get<User[]>(`${this.uri}/getApprovedTutors`);
  }

}
